import { Component } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {
  termoPesquisa: string = ''; // Variável para armazenar o termo de pesquisa

  pesquisar(): void {
    // Implemente aqui a lógica para pesquisar com base em 'this.termoPesquisa'
    // Você pode usar serviços ou fazer a pesquisa diretamente no componente
    console.log('Pesquisando por: ', this.termoPesquisa);
  }
}
<div class="container mt-5 pt-5">
  <form (submit)="pesquisar()">
    <div class="input-group mb-3">
      <input
        type="text"
        class="form-control"
        placeholder="Pesquisar Filme..."
        [(ngModel)]="termoPesquisa"
        name="termoPesquisa"
      />
      <button type="submit" class="btn btn-danger">Pesquisar</button>
    </div>
  </form>
</div>
